import React, { useState, useEffect } from 'react';

const API_KEY = 'AIzaSyAXI-yLqe4jfGv-abhQBNGDVOAXTEHThIY'; // Use your actual API key here

function App() {
  const [prompt, setPrompt] = useState('');
  const [conversation, setConversation] = useState([]);
  const [hoveredButton, setHoveredButton] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);
  const [uploadStatus, setUploadStatus] = useState('');

  useEffect(() => {
    // Load conversation history from localStorage on mount
    const savedConversation = localStorage.getItem('catetasConversation');
    if (savedConversation) {
      setConversation(JSON.parse(savedConversation));
    }
  }, []);

  useEffect(() => {
    // Save conversation history to localStorage on change
    localStorage.setItem('catetasConversation', JSON.stringify(conversation));
  }, [conversation]);

  const generateContent = async () => {
    if (!prompt.trim()) {
      alert('Please enter a prompt.');
      return;
    }

    // Append user prompt to conversation
    const newConversation = [...conversation, { sender: 'User', text: prompt }];
    setConversation(newConversation);

    try {
      // Call backend API to get AI response, with or without image
      let response;
      if (selectedImage) {
        const formData = new FormData();
        formData.append('image', selectedImage);
        formData.append('prompt', prompt);
        response = await fetch('http://localhost:5000/generate_with_image', {
          method: 'POST',
          body: formData,
        });
      } else {
        response = await fetch('http://localhost:5000/generate', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ prompt }),
        });
      }

      if (!response.ok) {
        throw new Error('Failed to get response from AI backend');
      }

      const data = await response.json();

      if (data.error) {
        throw new Error(data.error);
      }

      setConversation([...newConversation, { sender: 'AI', text: data.response }]);
      setPrompt('');
      setSelectedImage(null);
      setUploadStatus('');
    } catch (error) {
      alert('An error occurred: ' + error.message);
    }
  };

  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setSelectedImage(e.target.files[0]);
      setUploadStatus('');
    }
  };

  const uploadImage = async () => {
    if (!selectedImage) {
      alert('Please select an image to upload.');
      return;
    }
    const formData = new FormData();
    formData.append('image', selectedImage);

    try {
      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Failed to upload image');
      }

      const data = await response.json();

      if (data.error) {
        throw new Error(data.error);
      }

      setUploadStatus('Image uploaded successfully: ' + data.filename);
      setSelectedImage(null);
    } catch (error) {
      setUploadStatus('Upload failed: ' + error.message);
    }
  };

  const clearConversation = () => {
    setConversation([]);
    localStorage.removeItem('catetasConversation');
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      generateContent();
    }
  };

  return (
    <div style={styles.app}>
      <h1 style={styles.title}>Catetas AI</h1>
      <div style={styles.inputFrame}>
        <label style={styles.label} htmlFor="promptInput">Enter your prompt:</label>
        <textarea
          id="promptInput"
          style={styles.textarea}
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          onKeyDown={handleKeyDown}
          rows={5}
          placeholder="Type your prompt here..."
          onFocus={(e) => e.target.style.borderColor = '#8b5cf6'}
          onBlur={(e) => e.target.style.borderColor = '#6d28d9'}
        />
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
          style={{ display: 'none' }}
          id="imageInput"
        />
        <label
          htmlFor="imageInput"
          style={{
            display: 'inline-block',
            marginTop: 20,
            padding: '10px 20px',
            backgroundColor: '#7c3aed',
            color: '#f9fafb',
            borderRadius: 12,
            cursor: 'pointer',
            fontWeight: '700',
            boxShadow: '0 4px 12px rgba(124, 58, 237, 0.6)',
            transition: 'background-color 0.3s ease',
            userSelect: 'none',
          }}
          onMouseEnter={e => e.currentTarget.style.backgroundColor = '#a78bfa'}
          onMouseLeave={e => e.currentTarget.style.backgroundColor = '#7c3aed'}
        >
          {selectedImage ? `Selected: ${selectedImage.name}` : 'Select Image'}
        </label>
        <div style={{ marginTop: 10, marginBottom: 10 }}>
          <button
            style={{
              ...styles.button,
              ...styles.generateButton,
              width: '100%',
            }}
            onClick={generateContent}
            disabled={!prompt.trim() && !selectedImage}
          >
            Generate with Image
          </button>
        </div>
        <div style={{ color: '#22c55e', fontWeight: '700', minHeight: 24 }}>
          {uploadStatus}
        </div>
        <div style={styles.buttonsFrame}>
          <button
            style={{
              ...styles.button,
              ...styles.clearButton,
              ...(hoveredButton === 'clear' ? styles.buttonHover : {}),
            }}
            onClick={clearConversation}
            onMouseEnter={() => setHoveredButton('clear')}
            onMouseLeave={() => setHoveredButton(null)}
          >
            Clear Conversation
          </button>
          <button
            style={{
              ...styles.button,
              ...styles.generateButton,
              ...(hoveredButton === 'generate' ? styles.buttonHover : {}),
            }}
            onClick={generateContent}
            onMouseEnter={() => setHoveredButton('generate')}
      onMouseLeave={() => setHoveredButton(null)}
    >
      Generate
    </button>
    <button
      style={{
        ...styles.button,
        ...styles.generateButton,
        ...(hoveredButton === 'generateImage' ? styles.buttonHover : {}),
        marginLeft: 20,
        opacity: uploading ? 0.6 : 1,
        cursor: uploading ? 'not-allowed' : 'pointer',
      }}
      onClick={uploadImage}
      disabled={uploading}
      onMouseEnter={() => setHoveredButton('generateImage')}
      onMouseLeave={() => setHoveredButton(null)}
    >
      {uploading ? 'Uploading...' : 'Generate with Image'}
    </button>
  </div>
  <div style={{ marginTop: 20 }}>
    <input
      type="file"
      accept="image/*"
      onChange={(e) => setSelectedImage(e.target.files[0])}
      style={styles.fileInput}
    />
  </div>
</div>
      <div style={styles.historyFrame}>
        <label style={styles.label}>Conversation History:</label>
        <div style={styles.historyText}>
          {conversation.length === 0 && <p style={{ color: '#9ca3af' }}>No conversation yet.</p>}
          {conversation.map((entry, index) => (
            <div
              key={index}
              style={entry.sender === 'User' ? styles.userMessage : styles.aiMessage}
            >
              <div style={styles.messageHeader}>
                {entry.sender === 'User' ? '👤 User' : '🤖 AI'}
              </div>
              <p style={styles.messageText}>{entry.text}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

const styles = {
  app: {
    background: 'radial-gradient(circle at top left, #4c1d95, #7c3aed)',
    color: '#f9fafb',
    fontFamily: "'Roboto', Arial, Helvetica, sans-serif",
    maxWidth: 900,
    margin: '40px auto',
    padding: 40,
    borderRadius: 20,
    height: '90vh',
    display: 'flex',
    flexDirection: 'column',
    boxShadow: '0 16px 48px rgba(0,0,0,0.4)',
    position: 'relative',
    overflow: 'hidden',
  },
  title: {
    fontWeight: '900',
    fontSize: 36,
    marginBottom: 40,
    textAlign: 'center',
    textShadow: '3px 3px 8px rgba(0,0,0,0.5)',
    letterSpacing: '0.1em',
    userSelect: 'none',
  },
  inputFrame: {
    backgroundColor: '#5b21b6',
    padding: 24,
    borderRadius: 16,
    boxShadow: 'inset 0 0 20px rgba(0,0,0,0.4)',
    border: '1px solid #7c3aed',
    transition: 'background-color 0.3s ease',
  },
  label: {
    fontSize: 20,
    marginBottom: 14,
    display: 'block',
    color: '#d8d8d8',
    fontWeight: '700',
  },
  textarea: {
    width: '100%',
    fontFamily: "'Roboto Mono', Consolas, monospace",
    fontSize: 20,
